function capitalizarTexto(texto) {
    if (!texto || typeof texto !== 'string') return '';
    
    return texto.trim()
        .toLowerCase()
        .split(/\s+/)
        .map(palabra => {
            if (palabra.length === 0) return '';
            return palabra.charAt(0).toUpperCase() + palabra.slice(1);
        })
        .join(' ');
}

